'use client'

import { cn } from '@/lib/utils'
import { Loader2 } from 'lucide-react'
import { useFormStatus } from 'react-dom'
import { Button, ButtonProps } from './c-button'

export function ActionButton({ children, className, disabled, loading, ...props }: ButtonProps & { loading?: boolean }) {
	const { pending } = useFormStatus()

	return (
		<Button className={cn('relative', className)} disabled={disabled || loading || pending} type='submit' {...props}>
			<span className={cn('flex items-center gap-2', { invisible: loading || pending })}>{children}</span>
			{(loading || pending) && (
				<div className='absolute m-auto'>
					<Loader2 className='size-5 animate-spin' />
				</div>
			)}
		</Button>
	)
}

'use server'

import { ZodSchema } from 'zod'

export type ActionResult<T> = {
	success: boolean
	fieldErrors: Partial<Record<keyof T, string[]>>
	globalError: string | null
	values: Partial<Record<keyof T, string>>
}

type OnValidResult<T> = null | {
	globalError?: string
	fieldErrors?: Partial<Record<keyof T, string[]>>
}

export async function handleFormAction<T extends Record<string, unknown>>(
	formData: FormData,
	schema: ZodSchema<T>,
	onValid: (data: T) => Promise<OnValidResult<T>>
): Promise<ActionResult<T>> {
	const raw: Record<string, unknown> = {}
	for (const [key, value] of formData.entries()) {
		raw[key] = value === 'on' ? true : value
	}

	const parsed = schema.safeParse(raw)
	if (!parsed.success) {
		const fieldErrors = parsed.error.flatten().fieldErrors as Partial<Record<keyof T, string[]>>
		return {
			success: false,
			fieldErrors,
			globalError: null,
			values: Object.fromEntries(Object.entries(raw).map(([k, v]) => [k, String(v ?? '')])) as Partial<Record<keyof T, string>>,
		}
	}

	const result = await onValid(parsed.data)

	if (result?.fieldErrors || result?.globalError) {
		return {
			success: false,
			fieldErrors: result.fieldErrors ?? {},
			globalError: result.globalError ?? null,
			values: Object.fromEntries(Object.entries(parsed.data).map(([k, v]) => [k, String(v ?? '')])) as Partial<
				Record<keyof T, string>
			>,
		}
	}

	return {
		success: true,
		fieldErrors: {},
		globalError: null,
		values: {},
	}
}

import { cn } from '@/lib/utils'
import { VariantProps } from 'class-variance-authority'
import { Button as ButtonBase, buttonVariants } from '../button'

export type ButtonProps = React.ComponentProps<'button'> & VariantProps<typeof buttonVariants> & { asChild?: boolean }

export function Button({ className, ...props }: ButtonProps) {
	return <ButtonBase className={cn('cursor-pointer', className)} {...props} />
}

import { cn } from '@/lib/utils'
import { Input as InputBase } from '../input'

export function Input({ className, ...props }: React.ComponentProps<'input'>) {
	return <InputBase className={cn('focus-visible:ring', className)} {...props} />
}

'use client'

import { ReactNode, useActionState, useEffect, useState } from 'react'
import {
	AlertDialog,
	AlertDialogCancel,
	AlertDialogContent,
	AlertDialogDescription,
	AlertDialogFooter,
	AlertDialogHeader,
	AlertDialogTitle,
	AlertDialogTrigger,
} from '../alert-dialog'
import { ActionButton } from './action-button'

export function Confirm({
	action: _action,
	children,
	desc,
	title,
	variant = 'default',
}: {
	action: () => Promise<unknown>
	children: ReactNode
	desc: string
	title: string
	variant?: 'default' | 'destructive'
}) {
	const [open, setOpen] = useState(false)
	const [state, action] = useActionState(_action, null)

	useEffect(() => {
		if (state === undefined) setOpen(false)
	}, [state])

	return (
		<AlertDialog open={open} onOpenChange={setOpen}>
			<AlertDialogTrigger asChild>{children}</AlertDialogTrigger>
			<AlertDialogContent>
				<AlertDialogHeader>
					<AlertDialogTitle>{title}</AlertDialogTitle>
					<AlertDialogDescription>{desc}</AlertDialogDescription>
				</AlertDialogHeader>
				<AlertDialogFooter>
					<AlertDialogCancel className='cursor-pointer'>Cancel</AlertDialogCancel>
					<form action={action}>
						<ActionButton variant={variant}>Continue</ActionButton>
					</form>
				</AlertDialogFooter>
			</AlertDialogContent>
		</AlertDialog>
	)
}

export function ConfirmDelete({ action, children }: { action: () => Promise<unknown>; children: ReactNode }) {
	return (
		<Confirm
			action={action}
			title='Are you absolutely sure?'
			desc='This action cannot be undone. This will permanently delete this item and remove the data from our servers.'
			variant='destructive'>
			{children}
		</Confirm>
	)
}

'use client'

import { cn } from '@/lib/utils'
import { useEffect } from 'react'
import { useFormStatus } from 'react-dom'
import { toast } from 'sonner'

export function FormError({ hidden = false, value }: { hidden?: boolean; value: string[] | undefined }) {
	const { pending } = useFormStatus()

	useEffect(() => {
		if (!hidden) return
		if (value?.length && value.length > 0) toast.error(`FATAL: ${value[0]}`)
	}, [hidden, value])

	if (hidden || !value || !value.length) return null
	return <div className={cn('text-sm text-destructive', { hidden: pending })}>{value[0]}</div>
}

import { cn } from '@/lib/utils'
import { Label } from '../label'
import { Input } from './c-input'
import { FormError } from './form-error'
import { ActionState } from './types'

export function FormField({
	className,
	clearOnError = false,
	id: _id,
	label,
	name,
	state,
	...props
}: React.ComponentProps<'input'> & { clearOnError?: boolean; label: React.ReactNode | string; state?: ActionState }) {
	const id = String(name ?? _id)
	const value = clearOnError ? '' : state?.values?.[id] ?? ''
	const error = state?.fieldErrors?.[id]?.[0]

	return (
		<div className={cn('grid gap-2', className)}>
			{label && (typeof label === 'string' ? <Label htmlFor={id}>{label}</Label> : label)}
			<Input id={id} name={id} defaultValue={value} {...props} />
			<FormError value={error ? [error] : []} />
		</div>
	)
}

'use client'

import { usePathname, useRouter, useSearchParams } from 'next/navigation'
import { useEffect } from 'react'
import { toast } from 'sonner'
import { Toaster } from '../sonner'

export function GlobalToaster() {
	const pathname = usePathname()
	const router = useRouter()
	const searchParams = useSearchParams()

	useEffect(() => {
		const message = searchParams.get('message')
		const error = searchParams.get('error')
		const success = searchParams.get('success')

		if (!message && !error && !success) return
		const toastType = message ? toast : success ? toast.success : toast.error
		toastType(message ? message : success ? success : error)

		const newSearchParams = new URLSearchParams(searchParams.toString())
		const paramsToRemove = ['message', 'error', 'success']
		paramsToRemove.forEach((param) => newSearchParams.delete(param))
		const redirectPath = `${pathname}?${newSearchParams.toString()}`
		router.replace(redirectPath, { scroll: false })
	}, [pathname, router, searchParams])

	return <Toaster position='top-center' richColors />
}

export type ActionState = {
	values: Record<string, string>
	fieldErrors: Record<string, string[]>
	globalError: string | null
	success: boolean
}

'use client'

import { useActionState, useEffect } from 'react'
import { toast } from 'sonner'
import { ActionResult } from './action'

export function useForm<T>(action: (_: unknown, formData: FormData) => Promise<ActionResult<T>>) {
	const [formState, formAction] = useActionState(action, {
		success: false,
		fieldErrors: {},
		globalError: null,
		values: {},
	})

	useEffect(() => {
		if (!formState.globalError) return
		toast.error(formState.globalError)
	}, [formState.globalError])

	return [formState, formAction] as const
}
